mapper.select:
SELECT AC_KEY , AC_SALES_YN , AC_BUY_YN , AC_BUSI_NUM , AC_NM , AC_ADDR , AC_ADDR_DETAIL , AC_CEO_NM , AC_REP_TEL , AC_REP_FAX , REG_DT , REG_TM , REG_EMP_KEY , MOD_DT , MOD_TM , MOD_EMP_KEY FROM CMM_ACCOUNT_TB WHERE 1=1
AND AC_SALES_YN = #{acSalesYN}
AND AC_BUY_YN = #{acBuyYN}
AND AC_NM LIKE CONCAT('%', CONCAT(#{acNm}, '%'))
AND DELETE_YN='N' ORDER BY AC_NM ASC

mapper.insert:

INSERT INTO CMM_ACCOUNT_TB (
	AC_KEY
	, AC_BUSI_NUM
	, AC_SALES_YN
	, AC_BUY_YN
	, AC_NM
	, AC_ADDR
	, AC_ADDR_DETAIL
	, AC_CEO_NM
	, AC_REP_TEL
	, AC_REP_FAX
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
	, DELETE_YN
) VALUES (
	#{acKey}
	, #{acKey}
	, #{acSalesYn}
	, #{acBuyYn}
	, #{acNm}
	, #{acAddr}
	, #{acAddrDetail}
	, #{acCeoNm}
	, #{acRepTel}
	, #{acRepFax}
	, DATE_FORMAT(NOW(),'%Y%m%d')
	, DATE_FORMAT(NOW(),'%H%i%s')
	, #{regEmpKey}
	, 'N'
)

INSERT INTO CMM_ACCOUNT_DIRECTOR_TB (
	AC_KEY
	, AC_DIRECTOR_NM
	, AC_DIRECTOR_MB_NUM
	, AC_DIRECTOR_TEL_NUM
	, AC_DIRECTOR_EMAIL
	, AC_DIRECTOR_DEPT_NM
	, AC_DIRECTOR_POSITION_NM
	, AC_DIRECTOR_DESC
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
	, DELETE_YN
) VALUES

	(
		#{acKey}
		, #{item.acDirectorNm}
		, #{item.acDirectorMbNum}
		, #{item.acDirectorTelNum}
		, #{item.acDirectorEmail}
		, #{item.acDirectorDeptNm}
		, #{item.acDirectorPositionNm}
		, #{item.acDirectorDesc}
		, DATE_FORMAT(NOW(), '%Y%m%d')
		, DATE_FORMAT(NOW(), '%H%i%s')
		, #{regEmpKey}
		, 'N'
	)

INSERT INTO CMM_ACCOUNT_DEPOSIT_TB (
	AC_KEY
	, AC_AD_SEQ
	, AC_BANK_NM
	, AC_BKNO
	, AC_ACHOLDER
	, AC_REP_BKNO_YN
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
	, DELETE_YN
) VALUES

	(
		#{acKey}
		, (SELECT (SELECT COUNT(*) FROM CMM_ACCOUNT_DEPOSIT_TB CAD WHERE CAD.AC_KEY=#{acKey}) + 1)
		, #{item.acBankNm}
		, #{item.acBkno}
		, #{item.acAcholder}
		, #{item.acRepBknoYn}
		, DATE_FORMAT(NOW(), '%Y%m%d')
		, DATE_FORMAT(NOW(), '%H%i%s')
		, #{regEmpKey}
		, 'N'
	)
