mapper.insert:
INSERT INTO MT_PURCHASE_TB (
	BUY_KEY
	, BUY_FK_PJ_KEY
	, BUY_ORDER_FK_KEY
	, BUY_AMOUNT
	, YET_PAYMENT_AMOUNT
	, BUY_TURN
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
	, FK_PJ_KEY_TYPE
) VALUES (
	#{buyKey}
	, #{buyFkPjKey}
	, #{buyOrderFkKey}
	, #{buyAmount}
	, #{yetPaymentAmount}
	, #{buyTurn}
	, DATE_FORMAT(NOW(),'%Y%m%d')
	, DATE_FORMAT(NOW(),'%H%i%s')
	, #{regEmpKey}
	, #{fkPjKeyType}
)

mapper.update:

UPDATE MT_PURCHASE_TB
SET BUY_AMOUNT = #{buyAmount}
	, YET_PAYMENT_AMOUNT = #{yetPaymentAmount}
	, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d')
	, MOD_TM = DATE_FORMAT(NOW(),'%H%i%s') 
	, MOD_EMP_KEY = #{modEmpKey}
WHERE BUY_ORDER_FK_KEY=#{buyOrderFkKey}
	AND DONE_PAYMENT_AMOUNT = 0
	AND DELETE_YN='N'

mapper.select:
SELECT BUY_KEY AS buyKey , BUY_FK_PJ_KEY AS buyFkPjKey , BUY_ORDER_FK_KEY AS buyOrderFkKey , BUY_AMOUNT AS buyAmount , DONE_PAYMENT_AMOUNT AS donePaymentAmount , YET_PAYMENT_AMOUNT AS yetPaymentAmount , BUY_TURN AS buyTurn FROM MT_PURCHASE_TB WHERE 1=1
AND BUY_KEY = #{buyKey}
AND BUY_FK_PJ_KEY = #{buyFkPjKey}
AND BUY_ORDER_FK_KEY = #{buyOrderFkKey}
AND DELETE_YN='N'


SELECT
	SUM(DONE_PAYMENT_AMOUNT) AS donePaymentAmount
FROM MT_PURCHASE_TB
WHERE 1=1

	AND BUY_KEY = #{buyKey}


	AND BUY_FK_PJ_KEY = #{buyFkPjKey}


	AND BUY_ORDER_FK_KEY = #{buyOrderFkKey}

	AND DELETE_YN='N'

mapper.insert:
INSERT INTO MT_SALES_DETAIL_TB (
    SALES_KEY 
    , SALES_CT_FK_KEY 
    , SALES_YEAR_MONTH
    , SALES_TURN 
    , SALES_COLLECT_RATE 
    , SALES_TURN_AMOUNT 
    , SALES_CHARGE_DT 
    , SALES_BILL_FC_DT 
    , SALES_COLLECT_FC_DT
    , REG_DT 
    , REG_TM 
    , REG_EMP_KEY)
VALUES

    (
    #{item.salesKey} 
    , #{item.salesCtFkKey}
    , #{item.salesYearMonth}
    , #{item.salesTurn}
    , #{item.salesCollectRate}
    , #{item.salesTurnAmount}
    , #{item.salesCollectFcDt}
    , #{item.salesBillFcDt}
    , #{item.salesCollectFcDt}
    , DATE_FORMAT(NOW(),'%Y%m%d')
    , DATE_FORMAT(NOW(),'%H%i%s')
    , #{regEmpKey})

update.foreach:
UPDATE MT_SALES_DETAIL_TB
SET SALES_TURN=#{item.salesTurn} 
	, SALES_COLLECT_RATE = #{item.salesCollectRate}
	, SALES_TURN_AMOUNT = #{item.salesTurnAmount}
	, SALES_CHARGE_DT = #{item.salesCollectFcDt}
	, SALES_BILL_FC_DT = #{item.salesBillFcDt}
	, SALES_COLLECT_FC_DT = #{item.salesCollectFcDt}
	, DELETE_YN = 'N'
	, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d')
	, MOD_TM = DATE_FORMAT(NOW(),'%H%i%s') 
	, MOD_EMP_KEY = #{modEmpKey}
WHERE SALES_CT_FK_KEY = #{item.salesCtFkKey}
	AND SALES_YEAR_MONTH = #{item.salesYearMonth}

mapper.select:
SELECT SALES_KEY AS salesKey , SALES_CT_FK_KEY AS salesCtFkKey , SALES_YEAR_MONTH AS salesYearMonth , SALES_TURN AS salesTurn , SALES_COLLECT_RATE AS salesCollectRate , SALES_TURN_AMOUNT AS salesTurnAmount , SALES_CHARGE_DT AS salesChargeDt , SALES_BILL_FC_DT AS salesBillFcDt , SALES_COLLECT_FC_DT AS salesCollectFcDt , SALES_COLLECT_FINISH_DT AS salesCollectFinishDt , SALES_STATUS_CD AS salesStatusCd FROM MT_SALES_DETAIL_TB WHERE 1=1
AND SALES_CT_FK_KEY= #{salesCtFkKey}
AND SALES_KEY = #{salesKey}
AND DELETE_YN='N'

mapper.update:

UPDATE MT_SALES_DETAIL_TB SET
	DELETE_YN  = 'Y'
	, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d')
	, MOD_TM = DATE_FORMAT(NOW(),'%H%i%s') 
	, MOD_EMP_KEY = #{modEmpKey}
WHERE 1=1

	AND SALES_CT_FK_KEY= #{salesCtFkKey}


	AND SALES_KEY = #{salesKey}
