	, BILL_AC_DIRECTOR_NAME
	, BILL_AC_DIRECTOR_TEL
	, REMARK
	, REG_DT
	, REG_TM 
	, REG_EMP_KEY
)
VALUES
(
	  #{billCallKey}
	, #{pjKey}
	, #{billCtFkKey}
	, #{billTurnNo}
	, #{billAmount}
	, #{billIssueEmail}
	, #{billIssueStatus}
	, #{billIssueType}
	, #{billRequestDt}
	, #{billAcKey}
	, #{billAcDirectorName}
	, #{billAcDirectorTel}
	, #{remark}
	, DATE_FORMAT(NOW(),'%Y%m%d')
	, DATE_FORMAT(NOW(),'%k%i%s')
	, #{regEmpKey}
)

mapper.update:

	UPDATE MT_SALES_DETAIL_TB
	SET SALES_STATUS_CD = #{billIssueStatus}
	, SALES_COLLECT_FINISH_DT = #{salesCollectFinishDt} /* 수금완료일*/
	WHERE SALES_KEY       = #{billCtFkKey}

UPDATE MT_SALES_AMOUNT_TB SET DELETE_YN = 'N'	
		
		, MT_SALES_JAN_CALL_YN = #{mtSalesJanCallYnOrg}
		
		
		, MT_SALES_FEB_CALL_YN = #{mtSalesFebCallYnOrg}
		
		
		, MT_SALES_MAR_CALL_YN = #{mtSalesMarCallYnOrg}
		
		
		, MT_SALES_APR_CALL_YN = #{mtSalesAprCallYnOrg}
		
		
		, MT_SALES_MAY_CALL_YN = #{mtSalesMayCallYnOrg}
		
		
		, MT_SALES_JUN_CALL_YN = #{mtSalesJunCallYnOrg}
		
		
		, MT_SALES_JUL_CALL_YN = #{mtSalesJulCallYnOrg}
		
		
		, MT_SALES_AUG_CALL_YN = #{mtSalesAugCallYnOrg}
		
		
		, MT_SALES_SEP_CALL_YN = #{mtSalesSepCallYnOrg}
		
		
		, MT_SALES_OCT_CALL_YN = #{mtSalesOctCallYnOrg}
		
		
		, MT_SALES_NOV_CALL_YN = #{mtSalesNovCallYnOrg}
		
		
		, MT_SALES_DEC_CALL_YN = #{mtSalesDecCallYnOrg}
		
		, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d')
		, MOD_TM = DATE_FORMAT(NOW(),'%H%i%s')
		, MOD_EMP_KEY = #{modEmpKey}
WHERE MT_INTEGRATE_KEY =  #{mtIntegrateKey}	
	AND MT_SALES_YEAR = #{mtSalesYear}	/* yyyy */
	AND DELETE_YN='N'


	UPDATE MT_SD_BILLING_OP_TB
	SET   BILL_AMOUNT           = #{billAmount}
		, BILL_ISSUE_EMAIL      = #{billIssueEmail}
		, BILL_ISSUE_STATUS     = #{billIssueStatus}
		, BILL_ISSUE_TYPE       = #{billIssueType} 
		, BILL_REQUEST_DT       = #{billRequestDt}
		, BILL_AC_DIRECTOR_NAME = #{billAcDirectorName}
		, BILL_AC_DIRECTOR_TEL  = #{billAcDirectorTel}
		, REMARK                = #{remark}
		, MOD_DT                = DATE_FORMAT(NOW(),'%Y%m%d')
		, MOD_TM                = DATE_FORMAT(NOW(),'%k%i%s')
		, MOD_EMP_KEY           = #{regEmpKey}
	WHERE BILL_CALL_KEY         = #{billCallKey}


	UPDATE MT_SD_BILLING_OP_TB
	SET   DELETE_YN     = 'Y'
		, MOD_DT        = DATE_FORMAT(NOW(),'%Y%m%d')
		, MOD_TM        = DATE_FORMAT(NOW(),'%k%i%s')
		, MOD_EMP_KEY   = #{modEmpKey}
	WHERE BILL_CALL_KEY = #{billCallKey}

mapper.select:
SELECT PSB.BILL_FK_MT_KEY AS MT_KEY , PSB.BILL_TURN_NO AS BILL_TURN_NO /* 계산서 회차 */ , PSB.BILL_NO AS BILL_NO /* 계산서 번호, 승인번호 */ , PSB.BILL_CALL_KEY AS BILL_CALL_KEY /* 계산서요청번호 */ , PSB.BILL_ISSUE_DT AS BILL_ISSUE_DT /* 계산서 발행일*/ , PSB.BILL_AMOUNT AS BILL_AMOUNT /* 공급가액 */ , PSB.BILL_ISSUE_STATUS AS BILL_ISSUE_STATUS /* 계산서 상태 */ , PSB.REG_DT AS REG_DT /* 작성일자 */ , PSB.REMARK AS REMARK /* 요청사항 OR 비고 */ , PSB.BILL_ISSUE_TYPE AS BILL_ISSUE_TYPE /* 발행 타입 */ , PSD.SALES_KEY AS SALES_KEY /* 수금완료 처리를 위한 수금테이블 키 */ , PSB.SALES_COLLECT_FINISH_DT AS SALES_COLLECT_FINISH_DT /* 수금완료 일자 */ , PSB.BILL_AMOUNT AS SUPPLY_VALUE /* 공급가액 */ , CET.EMP_NM AS EMP_NM /* 계산서 요청자 등록자 */ , PSB.BILL_AC_KEY AS BILL_AC_KEY /* 매출처 사업자번호. */ , CAT.AC_NM AS BILL_AC_NM /* 매출처 상호 */ , PSB.BILL_AC_DIRECTOR_NAME AS BILL_AC_DIRECTOR_NM /* 매출처 담당자명 */ , PSB.BILL_AC_DIRECTOR_TEL AS BILL_AC_DIRECTOR_TEL /* 매출처 담당자전화 */ , PSB.BILL_ISSUE_EMAIL AS BILL_ISSUE_EMAIL /* 매출처 담당자이메일 */ , MCT.MT_NM AS MT_NM /* 유지보수 제목 */ , MCT.MT_AC_KEY AS MT_AC_KEY /* 거래처 사업자번호. */ , CAT2.AC_NM AS MT_AC_NM /* 거래처 상호 */ FROM MT_SD_BILLING_OP_TB PSB JOIN MT_CONTRACT_TB MCT ON PSB.BILL_FK_MT_KEY = MCT.MT_INTEGRATE_KEY AND MCT.DELETE_YN = 'N' JOIN MT_SALES_DETAIL_TB PSD ON PSB.BILL_CT_FK_KEY = PSD.SALES_KEY AND PSD.DELETE_YN = 'N' JOIN CMM_ACCOUNT_TB CAT ON PSB.BILL_AC_KEY = CAT.AC_KEY JOIN CMM_ACCOUNT_TB CAT2 ON MCT.MT_AC_KEY = CAT2.AC_KEY JOIN CMM_EMPLOYEE_TB CET ON PSB.REG_EMP_KEY = CET.EMP_KEY WHERE 1=1
AND PSB.BILL_FK_MT_KEY = #{pjKey}
AND PSD.SALES_KEY = #{billCtFkKey}
AND PSB.BILL_ISSUE_DT BETWEEN REPLACE(#{searchFromDate}, '-', '') AND REPLACE(#{searchToDate}, '-', '')
AND CAT.AC_NM LIKE CONCAT('%', CONCAT(#{searchAcNm}, '%'))
AND PSB.DELETE_YN = 'N' ORDER BY PSB.BILL_TURN_NO

mapper.insert:
INSERT INTO MT_PAYMENT_TB (  
	PAYMENT_KEY
	, PAYMENT_BUY_FK_KEY
	, MT_ORDER_TYPE
	, PAYMENT_STATUS_CD
	, PAYMENT_TURN
	, PAYMENT_CALL_DT
	, CALL_AMOUNT
	, PAYMENT_AC_FK_KEY
	, PAYMENT_ACC_SEQ_FK_KEY
	, BILL_FK_KEY
	, REMARK
    , DELETE_YN 
    , REG_DT 
    , REG_TM 
    , REG_EMP_KEY
) VALUES (
	#{paymentKey}
	, #{paymentBuyFkKey}
	, #{mtOrderType}
	, 'PYST2000'
	, (SELECT IFNULL(MAX(a.PAYMENT_TURN) + 1, 1) FROM MT_PAYMENT_TB a WHERE a.PAYMENT_BUY_FK_KEY=#{paymentBuyFkKey} AND DELETE_YN='N')
	, #{paymentCallDt}
	, #{callAmount}
	, #{paymentAcFkKey}
	, #{paymentAccSeqFkKey}
	, #{billFkKey}
	, #{remark}
    , 'N' 
    , DATE_FORMAT(NOW(),'%Y%m%d')
    , DATE_FORMAT(NOW(),'%H%i%s')
    , #{regEmpKey} 
)

mapper.update:
UPDATE MT_PAYMENT_TB SET
	DELETE_YN = 'Y'
	, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d') 
	, MOD_TM = DATE_FORMAT(NOW(),'%H%i%s')
	, MOD_EMP_KEY = #{modEmpKey}
WHERE PAYMENT_KEY = #{paymentKey}

UPDATE MT_PAYMENT_TB SET
