	, MOD_TM = DATE_FORMAT(NOW(),'%H%i%s')
	, MOD_EMP_KEY = #{modEmpKey}
WHERE PAYMENT_KEY = #{paymentKey}

UPDATE MT_PAYMENT_TB SET
	PAYMENT_STATUS_CD = 'PYST2000' /* 확인 상태로 변경 */
	, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d') 
	, MOD_TM = DATE_FORMAT(NOW(),'%H%i%s')
	, MOD_EMP_KEY = #{modEmpKey}
WHERE PAYMENT_KEY = #{paymentKey}
