    , MT_SALES_APR_AMOUNT 
    , MT_SALES_MAY_AMOUNT 
    , MT_SALES_JUN_AMOUNT 
    , MT_SALES_JUL_AMOUNT 
    , MT_SALES_AUG_AMOUNT 
    , MT_SALES_SEP_AMOUNT  
    , MT_SALES_OCT_AMOUNT  
    , MT_SALES_NOV_AMOUNT 
    , MT_SALES_DEC_AMOUNT 
    , REG_DT 
    , REG_TM 
    , REG_EMP_KEY)
VALUES

    (
    #{mtIntegrateKey} 
    , (SELECT IFNULL(MAX(a.MT_SALES_KEY) + 1, 1) FROM MT_SALES_AMOUNT_TB a WHERE a.MT_INTEGRATE_KEY=#{mtIntegrateKey}) 
    , #{item.mtSalesYear}
    , #{item.mtSalesJanAmount}
