mapper.select:
SELECT MA.MT_INTEGRATE_KEY , MA.MT_BUY_KEY , MA.MT_ORDER_KEY , MO.MT_ORDER_PAY_TERMS , MO.MT_ORDER_AC_KEY AS MT_ORDER_AC_KEY , CA.AC_NM AS MT_ORDER_AC_NM , MA.MT_BUY_YEAR , IFNULL(MA.MT_BUY_JAN_AMOUNT, 0) AS MT_BUY_JAN_AMOUNT , MA.MT_BUY_JAN_CALL_YN , IFNULL(MA.MT_BUY_FEB_AMOUNT, 0) AS MT_BUY_FEB_AMOUNT , MA.MT_BUY_FEB_CALL_YN , IFNULL(MA.MT_BUY_MAR_AMOUNT, 0) AS MT_BUY_MAR_AMOUNT , MA.MT_BUY_MAR_CALL_YN , IFNULL(MA.MT_BUY_APR_AMOUNT, 0) AS MT_BUY_APR_AMOUNT , MA.MT_BUY_APR_CALL_YN , IFNULL(MA.MT_BUY_MAY_AMOUNT, 0) AS MT_BUY_MAY_AMOUNT , MA.MT_BUY_MAY_CALL_YN , IFNULL(MA.MT_BUY_JUN_AMOUNT, 0) AS MT_BUY_JUN_AMOUNT , MA.MT_BUY_JUN_CALL_YN , IFNULL(MA.MT_BUY_JUL_AMOUNT, 0) AS MT_BUY_JUL_AMOUNT , MA.MT_BUY_JUL_CALL_YN , IFNULL(MA.MT_BUY_AUG_AMOUNT, 0) AS MT_BUY_AUG_AMOUNT , MA.MT_BUY_AUG_CALL_YN , IFNULL(MA.MT_BUY_SEP_AMOUNT, 0) AS MT_BUY_SEP_AMOUNT , MA.MT_BUY_SEP_CALL_YN , IFNULL(MA.MT_BUY_OCT_AMOUNT, 0) AS MT_BUY_OCT_AMOUNT , MA.MT_BUY_OCT_CALL_YN , IFNULL(MA.MT_BUY_NOV_AMOUNT, 0) AS MT_BUY_NOV_AMOUNT , MA.MT_BUY_NOV_CALL_YN , IFNULL(MA.MT_BUY_DEC_AMOUNT, 0) AS MT_BUY_DEC_AMOUNT , MA.MT_BUY_DEC_CALL_YN FROM MT_BUY_AMOUNT_TB MA, MT_BACK_ORDER_TB MO, CMM_ACCOUNT_TB CA WHERE MA.MT_INTEGRATE_KEY = #{mtIntegrateKey}
AND MA.MT_ORDER_KEY = #{mtOrderKey}
AND MA.MT_BUY_KEY = #{mtBuyKey}
AND MA.MT_ORDER_KEY = MO.MT_ORDER_KEY AND MO.MT_ORDER_AC_KEY = CA.AC_KEY AND MA.DELETE_YN = 'N' ORDER BY MO.MT_ORDER_AC_KEY, MA.MT_BUY_YEAR

