	#{gbKey}
	, #{mtIntegrateKey}
	, #{gbKindCd}
	, #{gbStartDt}
	, #{gbEndDt}
	, 'R'
	, #{gbRate}
	, #{remark}
	, DATE_FORMAT(NOW(),'%Y%m%d')
	, DATE_FORMAT(NOW(),'%H%i%s')
	, #{regEmpKey} 
)

