	  , PJ_END_DT          = #{pjEndDt}
	  , PJ_SALE_EMP_KEY    = #{pjSaleEmpKey}
	  , PJ_SUPPORT_EMP_KEY = #{pjSupportEmpKey}
	  , REMARK             = #{remark}
	  , MOD_DT             = DATE_FORMAT(NOW(),'%Y%m%d')
	  , MOD_TM             = DATE_FORMAT(NOW(),'%H%i%s')

	  , SP_KEY             = #{spKey}

WHERE  PJ_KEY = #{pjKey}


