mapper.select:
SELECT IFNULL(SUM(DAT.SALES_AMOUNT), 0) AS SALES_AMOUNT FROM ( SELECT PSD.SALES_TURN_AMOUNT AS SALES_AMOUNT /* 수금액(부가세별도) */ FROM PJ_SALES_DETAIL_TB PSD JOIN PJ_MAIN_TB PMT ON PSD.SALES_CT_FK_KEY = PMT.PJ_KEY AND PMT.DELETE_YN = 'N' JOIN PJ_CONTRACT_TB PCT ON PMT.PJ_KEY = PCT.PJ_KEY AND PCT.DELETE_YN = 'N' JOIN CMM_ACCOUNT_TB CAT ON PMT.AC_KEY = CAT.AC_KEY WHERE PSD.DELETE_YN = 'N' AND PSD.SALES_COLLECT_FC_DT BETWEEN REPLACE(#{searchFromDt}, '-', '') AND REPLACE(#{searchToDt}, '-', '')
AND CAT.AC_NM LIKE CONCAT('%', CONCAT(#{searchAcNm}, '%'))
) DAT

SELECT DAT.PJ_KEY AS PJ_KEY , DAT.BUY_KEY AS BUY_KEY , DAT.PAYMENT_STATUS_CD AS PAYMENT_STATUS_CD , DAT.CT_DT AS CT_DT , DAT.PJ_NM AS PJ_NM , DAT.AC_NM AS AC_NM , DAT.CALL_AMOUNT_ORG AS CALL_AMOUNT_ORG , DAT.EMP_NM AS EMP_NM , DAT.CALL_AMOUNT AS CALL_AMOUNT , DAT.PAYMENT_DT AS PAYMENT_DT FROM ( SELECT PMT.PJ_KEY AS PJ_KEY , PUR.BUY_KEY AS BUY_KEY , PPT.PAYMENT_STATUS_CD AS PAYMENT_STATUS_CD , PCT.CT_DT AS CT_DT /* 거래일자*/ , PMT.PJ_NM AS PJ_NM /* 프로젝트명 */ , CAT.AC_NM AS AC_NM /* 거래처명 */ , PPT.CALL_AMOUNT AS CALL_AMOUNT_ORG /* 지급액 */ , CET.EMP_NM AS EMP_NM /* 영업담당자 */ , PPT.CALL_AMOUNT AS CALL_AMOUNT /* 지급액(부가세별도) */ , PPT.PAYMENT_DT AS PAYMENT_DT /* 지급일정 */ FROM PJ_PAYMENT_TB PPT JOIN PJ_PURCHASE_TB PUR ON PUR.BUY_KEY = PPT.PAYMENT_BUY_FK_KEY AND PUR.DELETE_YN = 'N' JOIN PJ_MAIN_TB PMT ON PMT.PJ_KEY = PUR.BUY_FK_PJ_KEY AND PMT.DELETE_YN = 'N' JOIN PJ_CONTRACT_TB PCT ON PMT.PJ_KEY = PCT.PJ_KEY AND PCT.DELETE_YN = 'N' JOIN CMM_CODE_MNG_TB CCM ON PPT.PAYMENT_STATUS_CD = CCM.CD_KEY JOIN CMM_ACCOUNT_TB CAT ON PMT.AC_KEY = CAT.AC_KEY JOIN CMM_EMPLOYEE_TB CET ON PMT.PJ_SALE_EMP_KEY = CET.EMP_KEY WHERE PPT.DELETE_YN = 'N' AND PPT.PAYMENT_DT BETWEEN REPLACE(#{searchFromDt}, '-', '') AND REPLACE(#{searchToDt}, '-', '')
AND CAT.AC_NM LIKE CONCAT('%', CONCAT(#{searchAcNm}, '%'))
) DAT ORDER BY DAT.PAYMENT_DT ASC

SELECT IFNULL(SUM(DAT.CALL_AMOUNT), 0) AS CALL_AMOUNT FROM ( SELECT PPT.CALL_AMOUNT AS CALL_AMOUNT /* 지급액(부가세별도) */ FROM PJ_PAYMENT_TB PPT JOIN PJ_PURCHASE_TB PUR ON PUR.BUY_KEY = PPT.PAYMENT_BUY_FK_KEY AND PUR.DELETE_YN = 'N' JOIN PJ_MAIN_TB PMT ON PMT.PJ_KEY = PUR.BUY_FK_PJ_KEY AND PMT.DELETE_YN = 'N' JOIN CMM_ACCOUNT_TB CAT ON PMT.AC_KEY = CAT.AC_KEY WHERE PPT.DELETE_YN = 'N' AND PPT.PAYMENT_DT BETWEEN REPLACE(#{searchFromDt}, '-', '') AND REPLACE(#{searchToDt}, '-', '')
AND CAT.AC_NM LIKE CONCAT('%', CONCAT(#{searchAcNm}, '%'))
) DAT
