mapper.insert:
INSERT INTO MT_CONTRACT_TB (
	MT_INTEGRATE_KEY
	, MT_CT_KEY
	, MT_SEQ
	, MT_AC_KEY
	, MT_AC_DIRECTOR_KEY
	, MT_NM
	, MT_CT_DT
	, MT_START_DT
	, MT_END_DT
	, MT_RG_INSPECT_CNT
	, MT_IM_CD
	, MT_SB_CT_YN
	, GB_YN
	, MT_SALE_EMP_KEY
	, MT_MNG_EMP_KEY
	, MT_SUPPORT_EMP_KEY
	, MT_AMOUNT
	, MT_PAY_TERMS
	, REMARK
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
) VALUES (
	(SELECT IFNULL(MAX(MT_INTEGRATE_KEY) + 1, 1) FROM MT_CONTRACT_TB)


	, #{mtCtKey}
	(SELECT MAX(MT_SEQ) + 1 FROM MT_CONTRACT_TB WHERE MT_CT_KEY=#{mtCtKey})



	, make_key('MT')
	, 1			

	, #{mtCtKey}
	, #{acDirectorKey}
	, #{mtNm}
	, #{ctDt}
	, #{startDt}
	, #{endDt}
	, #{rgInspectCnt}
	, #{imCd}
	, #{sbCtYn}
	, #{gbYn}
	, #{saleEmpKey}
	, #{mngEmpKey}
	, #{supportEmpKey}
	, #{amount}
	, #{payTerms}
	, #{remark}
	, DATE_FORMAT(NOW(),'%Y%m%d')
	, DATE_FORMAT(NOW(),'%k%i%s')
	, #{regEmpKey}
);

mapper.select:
SELECT @rownum:=@rownum+1 AS ROW_NUM , M.* FROM ( SELECT MC.MT_CT_KEY , MC.MT_INTEGRATE_KEY , MC.MT_AC_KEY , CA.AC_NM AS MT_AC_NM , MC.MT_NM , MC.MT_CT_DT , MC.MT_START_DT , MC.MT_END_DT , MC.MT_RG_INSPECT_CNT , MC.MT_IM_CD , MC.MT_SB_CT_YN , (SELECT EMP_NM FROM CMM_EMPLOYEE_TB WHERE EMP_KEY= MC.MT_SALE_EMP_KEY) AS SALE_EMP_NM , (SELECT EMP_NM FROM CMM_EMPLOYEE_TB WHERE EMP_KEY= MC.MT_MNG_EMP_KEY) AS MNG_EMP_NM , (SELECT EMP_NM FROM CMM_EMPLOYEE_TB WHERE EMP_KEY= MC.MT_SUPPORT_EMP_KEY) AS SUPPORT_EMP_NM FROM MT_CONTRACT_TB MC, CMM_ACCOUNT_TB CA, (SELECT @ROWNUM := 0) AS R WHERE 1=1
AND MT_CT_DT LIKE CONCAT(year(NOW()),'%')
AND MT_CT_DT >= #{fromDate}
AND MT_CT_DT <= #{toDate}
AND MT_SALE_EMP_KEY = #{searchSaleEmpKey}
AND MT_NM LIKE CONCAT(#{searchMtName}, '%')
AND MC.MT_AC_KEY = CA.AC_KEY AND DELETE_YN='N' ORDER BY MT_CT_DT DESC ) M

mapper.update:
UPDATE MT_CONTRACT_TB
SET DELETE_YN='Y'
	, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d')
	, MOD_TM = DATE_FORMAT(NOW(),'%k%i%s')
	, MOD_EMP_KEY = #{modEmpKey}
WHERE MT_INTEGRATE_KEY=#{mtIntegrateKey}
