mapper.update:

UPDATE PJ_ORDER_TB
SET    DELETE_YN = 'Y'
     , MOD_EMP_KEY = #{regEmpKey}
     , MOD_DT      = DATE_FORMAT(NOW(),'%Y%m%d')
	 , MOD_TM      = DATE_FORMAT(NOW(),'%H%i%s') 
WHERE  PJ_ORDER_KEY = #{orderKey}

mapper.insert:

INSERT INTO PJ_ORDER_PRODUCT_TB 
(
	  PJ_ORDER_KEY
	, ORDER_SEQ
	, ORDER_PM_FK_KEY
	, ORDER_QUANTITY
	, ORDER_UPRICE
	, ORDER_TOTAL_AMOUNT
	, ORDER_KIND
	, REMARK
	, ORDER_RECEIPT_DT
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
)
VALUES
(
	  #{orderKey} 
	, #{orderSeq} 
	, #{orderPmFkKey} 
	, #{orderQuantity} 
	, #{orderUprice} 
	, #{totalAmount} 
	, #{orderCategory} 
	, #{remark} 
	, #{orderReceiptDt} 
	, DATE_FORMAT(NOW(),'%Y%m%d') 
	, DATE_FORMAT(NOW(),'%k%i%s') 
	, #{regEmpKey}
)


INSERT INTO PJ_ORDER_TB
(
	  PJ_ORDER_KEY
	, ORDER_CT_FK_KEY
	, ORDER_DT
	, ORDER_AC_KEY
	, ORDER_AC_DIRECTOR_KEY
	, ORDER_PAY_TERMS
	, TAX_YN
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
	, ORDER_AMOUNT
	, BILL_MAPPING_NUM
)
VALUES
(
	  #{orderKey} 
	, #{orderCtFkKey} 
	, REPLACE(#{orderDt}, '-', '')
	, #{orderAcKey} 
	, #{orderAcDirectorKey} 
	, #{orderPayTerms} 
	, #{taxYn} 
	, DATE_FORMAT(NOW(),'%Y%m%d') 
	, DATE_FORMAT(NOW(),'%k%i%s') 
	, #{regEmpKey} 
	, #{orderAmount}  
	, #{billMappingNum} 
)

mapper.update:

UPDATE PJ_ORDER_TB
SET    ORDER_CT_FK_KEY       = #{orderCtFkKey}
	 , ORDER_DT              = REPLACE(#{orderDt}, '-', '')
	 , ORDER_AC_KEY          = #{orderAcKey}
	 , ORDER_AC_DIRECTOR_KEY = #{orderAcDirectorKey}
	 , ORDER_PAY_TERMS       = #{orderPayTerms} 
	 , TAX_YN                = #{taxYn}
	 , MOD_DT                = DATE_FORMAT(NOW(),'%Y%m%d')
	 , MOD_TM                = DATE_FORMAT(NOW(),'%k%i%s') 
	 , MOD_EMP_KEY           = #{regEmpKey}
	 , ORDER_AMOUNT          = #{orderAmount}
	 , BILL_MAPPING_NUM      = #{billMappingNum}
WHERE PJ_ORDER_KEY = #{orderKey}
