mapper.insert:
INSERT INTO PM_PRODUCT_MNG_TB (
	  PM_KEY
	, PM_CLASS_CD
	, PM_DETAIL_CLASS_CD
	, PM_LINE_CD
	, PM_NM_CD
	, MF_AC_KEY
	, PM_RELEASE_DT
	, EOL_DT
	, EOSL_DT
	, PM_REMARK
	, REG_DT
	, REG_TM
	, REG_EMP_KEY
	, DELETE_YN
)
VALUES
(
	  #{pmKey}
	, #{pmClassCd}
	, #{pmDetailClassCd}
	, #{pmLineCd}
	, #{pmNmCd}
	, #{mfAcKey}
	, #{pmReleaseDt}
	, #{eolDt}
	, #{eoslDt}
	, #{pmRemark}
	, DATE_FORMAT(NOW(),'%Y%m%d')
	, DATE_FORMAT(NOW(),'%k%i%s')
	, #{regEmpKey}
	,'N'
)

mapper.update:

UPDATE PM_PRODUCT_MNG_TB
SET
	 PM_CLASS_CD = #{pmClassCd}
	, PM_DETAIL_CLASS_CD = #{pmDetailClassCd}
	, PM_LINE_CD = #{pmLineCd}
	, PM_NM_CD = #{pmNmCd}
	, MF_AC_KEY = #{mfAcKey}

		, PM_RELEASE_DT = #{pmReleaseDt}


		, EOL_DT = #{eolDt}


		, EOSL_DT = #{eoslDt}


		, PM_REMARK = #{pmRemark}

	, MOD_DT = DATE_FORMAT(NOW(),'%Y%m%d')
	, MOD_TM = DATE_FORMAT(NOW(),'%k%i%s')
	, MOD_EMP_KEY = #{modEmpKey}
WHERE PM_KEY = #{pmKey}
AND DELETE_YN = 'N'
